import { useRef, useState } from 'react';
import './App.css';

function App() {
const[x , setx] = useState([])

const inputRef = useRef()

const add= ()=>{
      const value = inputRef.current.value
      setx({...x , value})

}
  return (
    <div className="App">



       <h2>
            To Do List
       </h2>
       <ul>
           {
            x.map((item) => {
              return<li>{item}</li>
            })

           }
      </ul>

      <input ref={inputRef} placeholder='Enter New Task'/> 
      <button onClick={add} >Add</button>

    </div>
  );
}

export default App;
